  computed: {
    itemsByPlace() {
      let data  = this.items[this.currentHotel].data
      return data.map(d => {
        d.price = this.minPrice(d.offers)
        return d
      })
    },
    prepareItems() {
      return this.items.map(item => {
        item.price = this.calculateMiddlePrice(item.data)
        item.count = item.data.length
        return item
      })
    }
  },
  methods: {
    minPrice(offers) {
      let price
      offers.map(o => {
        if (!price || price > +o.price.value) price = +o.price.value
      })

      return price
    },
    calculateMiddlePrice(arr) {
      let sum = 0
      arr.map( a => {
        sum += this.minPrice(a.offers)
      })

      return sum / arr.length
    },
    deleteItem(item) {
      this.items[this.currentHotel].data =
        this.items[this.currentHotel].data.filter(x => x !== item)
    },
    openLink(id) {
      let base = 'https://travel.yandex.ru/hotels/'
      window.open(base + id, '_blank');
    }
  },
  filters: {
    priceF(val) {
      val = val.toFixed(2).toString();
      var separator = " ";
      return val.replace(/(\d{1,3}(?=(?:\d\d\d)+(?!\d)))/g, "$1" + separator) + ' руб.'

    }
  }
